import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Fondo } from '../models/fondo.model';
import { FondoService } from '../services/Fondo.service';


@Component({
  selector: 'app-crear-fondo',
  templateUrl: './crear-fondos.component.html',
  styleUrls: ['./crear-fondos.component.css']
})
export class CrearFondoComponent {
  fondo: Fondo = {
    fondoId: '',
    nombreFondo: '',
    descripcion: '',
    montoMinimo: 0,
    fechaSuscripcion: ''
  };

  constructor(private fondoService: Service) {}



  crearFondo() {
    this.fondoService.crearFondo(this.cliente).subscribe(
      response => {
        console.log('Cliente creado:', response);
        this.mostrarMensaje = true; // Mostrar mensaje de confirmación
        this.cliente = {
          id: 'vacio',
          nombres: '',
          apellidos: '',
          tipoIdentificacion: '',
          numeroIdentificacion: '',
          email: '',
          telefono: '',
          saldoActual: 0,
          preferenciaNotificacion: '',
          fondosSuscritos: [],
          historialTransacciones: []
        };
      },
      error => {
        console.error('Error al crear cliente:', error);
        // Manejo de errores, si es necesario
      }
    );
  }
}
